import { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Chat({ token, user }) {
    const [messages, setMessages] = useState([]);
    const [inputMessage, setInputMessage] = useState('');
    const [status, setStatus] = useState('disconnected');
    const [error, setError] = useState(null);
    const wsRef = useRef(null);
    const reconnectTimerRef = useRef(null);
    const navigate = useNavigate();

    const connectWebSocket = () => {
        if (wsRef.current) {
            wsRef.current.close();
            wsRef.current = null;
        }

        if (reconnectTimerRef.current) {
            clearTimeout(reconnectTimerRef.current);
            reconnectTimerRef.current = null;
        }

        setStatus('connecting');
        setError(null);

        try {
            const socket = new WebSocket(`ws://localhost:8081/ws${token ? `?token=${encodeURIComponent(token)}` : ''}`);
            wsRef.current = socket;

            socket.onopen = () => {
                console.log('WebSocket connected');
                setStatus('connected');
                setError(null);

                if (token) {
                    socket.send(JSON.stringify({
                        type: 'get_history',
                        limit: 50
                    }));
                }
            };

            socket.onmessage = (event) => {
                try {
                    const data = JSON.parse(event.data);
                    if (Array.isArray(data)) {
                        setMessages(data);
                    } else {
                        setMessages(prev => [...prev, data]);
                    }
                } catch (parseError) {
                    console.error('Error parsing message:', parseError);
                    setError('Failed to parse message');
                }
            };

            socket.onerror = (errorEvent) => {
                console.error('WebSocket error:', errorEvent);
                setError('Connection error');
                setStatus('error');
            };

            socket.onclose = (closeEvent) => {
                console.log('WebSocket closed:', closeEvent.code, closeEvent.reason);
                setStatus('disconnected');

                if (closeEvent.code !== 1000) {
                    setError('Reconnecting...');
                    reconnectTimerRef.current = setTimeout(() => {
                        connectWebSocket();
                    }, 3000);
                }
            };
        } catch (setupError) {
            console.error('WebSocket setup failed:', setupError);
            setError('Connection setup failed');
            setStatus('error');
        }
    };

    useEffect(() => {
        connectWebSocket();
        return () => {
            if (wsRef.current) {
                wsRef.current.close();
            }
            if (reconnectTimerRef.current) {
                clearTimeout(reconnectTimerRef.current);
            }
        };
    }, [token]);

    const sendMessage = () => {
        if (!inputMessage.trim() || !token) return;

        try {
            if (!wsRef.current || wsRef.current.readyState !== WebSocket.OPEN) {
                throw new Error('Connection not ready');
            }

            const message = {
                type: 1,
                content: inputMessage,
                sender: user?.username || 'Guest',
                userId: user?.id || 0,
                timestamp: Date.now()
            };

            wsRef.current.send(JSON.stringify(message));
            setInputMessage('');
        } catch (sendError) {
            console.error('Error sending message:', sendError);
            setError('Failed to send message');
            setStatus('error');
        }
    };

    const handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            sendMessage();
        }
    };

    return (
        <div className="chat-container">
            <div className="chat-header">
                <h2>Community Chat</h2>
                <div className={`connection-status ${status}`}>
                    Status: {status} {error && `(${error})`}
                </div>
            </div>

            <div className="messages-container">
                {messages.length > 0 ? (
                    messages.map((msg, index) => (
                        <div
                            key={`${msg.timestamp}-${index}`}
                            className={`message ${msg.userId === user?.id ? 'own-message' : ''}`}
                        >
                            <div className="message-sender">{msg.sender}</div>
                            <div className="message-content">{msg.content}</div>
                            <div className="message-time">
                                {new Date(msg.timestamp).toLocaleTimeString()}
                            </div>
                        </div>
                    ))
                ) : (
                    <div className="no-messages">No messages yet</div>
                )}
            </div>

            {token ? (
                <div className="message-input-container">
                    <input
                        type="text"
                        value={inputMessage}
                        onChange={(e) => setInputMessage(e.target.value)}
                        onKeyPress={handleKeyPress}
                        placeholder="Type your message..."
                        disabled={status !== 'connected'}
                    />
                    <button
                        onClick={sendMessage}
                        disabled={!inputMessage.trim() || status !== 'connected'}
                    >
                        Send
                    </button>
                </div>
            ) : (
                <div className="login-notice">
                    Please login to send messages
                </div>
            )}
        </div>
    );
}
