import axios from 'axios';

const API_BASE = 'http://localhost:8080/api/v1';
const FORUM_API = 'http://localhost:8081/api';

// Создаем экземпляр axios с базовыми настройками
const apiClient = axios.create({
    baseURL: API_BASE,
    withCredentials: true,
    timeout: 10000
});

const forumClient = axios.create({
    baseURL: FORUM_API,
    timeout: 10000
});

// Добавляем интерсептор для авторизации
forumClient.interceptors.request.use(config => {
    const token = localStorage.getItem('token');
    if (token) {
        config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
});

export const authAPI = {
    register: (userData) => apiClient.post('/register', userData),
    login: (credentials) => apiClient.post('/login', credentials)
};

export const forumAPI = {
    getPosts: () => forumClient.get('/posts'),
    createPost: (postData) => forumClient.post('/posts', postData),
    deletePost: (postId) => forumClient.delete(`/posts/${postId}`)
};

// Обработка ошибок
const handleError = (error) => {
    if (error.response) {
        console.error('API Error:', error.response.data);
        throw new Error(error.response.data.error || 'Request failed');
    } else {
        console.error('Network Error:', error.message);
        throw new Error('Network error. Please try again.');
    }
};

// Обертки для удобства
export const registerUser = async (userData) => {
    try {
        return await authAPI.register(userData);
    } catch (error) {
        return handleError(error);
    }
};

export const loginUser = async (credentials) => {
    try {
        return await authAPI.login(credentials);
    } catch (error) {
        return handleError(error);
    }
};
