import { useState, useEffect, useRef, useCallback } from 'react';

export default function Chat({ token, user }) {
    const [messages, setMessages] = useState([]);
    const [inputMessage, setInputMessage] = useState('');
    const [status, setStatus] = useState('disconnected');
    const wsRef = useRef(null);
    const reconnectAttempts = useRef(0);
    const messageQueue = useRef([]);

    const connectWebSocket = useCallback(() => {
        if (wsRef.current?.readyState === WebSocket.OPEN) return;

        const socket = new WebSocket(`ws://localhost:8081/ws${token ? `?token=${encodeURIComponent(token)}` : ''}`);
        wsRef.current = socket;

        socket.onopen = () => {
            setStatus('connected');
            reconnectAttempts.current = 0;

            // Отправляем все сообщения из очереди
            while (messageQueue.current.length > 0) {
                const msg = messageQueue.current.shift();
                socket.send(JSON.stringify(msg));
            }

            // Загружаем историю сообщений
            socket.send(JSON.stringify({
                type: 'get_history',
                limit: 100
            }));
        };

        socket.onmessage = (event) => {
            const data = JSON.parse(event.data);
            setMessages(prev => Array.isArray(data) ? data : [...prev, data]);
        };

        socket.onclose = () => {
            setStatus('disconnected');
            const delay = Math.min(3000 * (reconnectAttempts.current + 1), 30000);
            setTimeout(connectWebSocket, delay);
            reconnectAttempts.current++;
        };

        socket.onerror = (error) => {
            console.error('WebSocket error:', error);
            setStatus('error');
        };
    }, [token]);

    const sendMessage = () => {
        if (!inputMessage.trim() || !token) return;

        const message = {
            type: 1,
            content: inputMessage,
            sender: user.username,
            userId: user.id,
            timestamp: Date.now()
        };

        if (wsRef.current?.readyState === WebSocket.OPEN) {
            wsRef.current.send(JSON.stringify(message));
        } else {
            messageQueue.current.push(message);
        }

        setInputMessage('');
    };

    const deleteMessage = (messageId) => {
        if (!token) return;
        const message = {
            type: 'delete_message',
            messageId,
            userId: user.id
        };

        if (wsRef.current?.readyState === WebSocket.OPEN) {
            wsRef.current.send(JSON.stringify(message));
        } else {
            messageQueue.current.push(message);
        }
    };

    useEffect(() => {
        connectWebSocket();

        const handleVisibilityChange = () => {
            if (document.visibilityState === 'visible' && status !== 'connected') {
                connectWebSocket();
            }
        };

        document.addEventListener('visibilitychange', handleVisibilityChange);

        return () => {
            document.removeEventListener('visibilitychange', handleVisibilityChange);
            if (wsRef.current) {
                wsRef.current.close();
            }
        };
    }, [connectWebSocket, status]);

    return (
        <div className="chat-container">
            <div className="chat-header">
                <h2>Community Chat</h2>
                <div className={`connection-status ${status}`}>
                    {status === 'connected' ? 'Online' : 'Offline'}
                </div>
            </div>

            <div className="messages-container">
                {messages.map((msg) => (
                    <div key={msg.id} className={`message ${msg.userId === user?.id ? 'own-message' : ''}`}>
                        <div className="message-header">
                            <span className="message-sender">{msg.sender}</span>
                            <span className="message-time">
                                {new Date(msg.timestamp).toLocaleString()}
                            </span>
                            {msg.userId === user?.id && (
                                <button
                                    className="delete-message-btn"
                                    onClick={() => deleteMessage(msg.id)}
                                >
                                    ×
                                </button>
                            )}
                        </div>
                        <div className="message-content">{msg.content}</div>
                    </div>
                ))}
            </div>

            {token ? (
                <div className="message-input-container">
                    <input
                        type="text"
                        value={inputMessage}
                        onChange={(e) => setInputMessage(e.target.value)}
                        onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
                        placeholder="Type your message..."
                        disabled={status !== 'connected'}
                    />
                    <button
                        onClick={sendMessage}
                        disabled={!inputMessage.trim() || status !== 'connected'}
                    >
                        Send
                    </button>
                </div>
            ) : (
                <div className="login-notice">
                    Please login to send messages
                </div>
            )}
        </div>
    );
}
