import { useState, useEffect, useRef } from 'react';

export default function Chat({ token, user }) {
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState('');
    const socketRef = useRef(null);

    // Подключение к WebSocket
    useEffect(() => {
        if (!token) {
            console.error('Token is required for WebSocket connection');
            return;
        }

        // Создаем новое подключение
        const socket = new WebSocket(`ws://localhost:8081/ws?token=${token}`);
        socketRef.current = socket;

        socket.onopen = () => {
            console.log('WebSocket connection established');
        };

        socket.onmessage = (event) => {
            try {
                const newMessage = JSON.parse(event.data);
                setMessages(prev => [...prev, newMessage]);
            } catch (error) {
                console.error('Error parsing WebSocket message:', error);
            }
        };

        socket.onerror = (error) => {
            console.error('WebSocket error:', error);
        };

        socket.onclose = () => {
            console.log('WebSocket connection closed');
        };

        // Очистка при размонтировании компонента
        return () => {
            if (socketRef.current?.readyState === WebSocket.OPEN) {
                socketRef.current.close();
            }
        };
    }, [token]);

    // Отправка сообщения
    const sendMessage = () => {
        if (!message.trim()) return;

        if (socketRef.current?.readyState === WebSocket.OPEN) {
            const messageData = {
                Content: message,
                Type: 1, // 1 - обычное сообщение чата
                Sender: user?.username || 'Guest',
                UserID: user?.id || 0,
                Timestamp: Date.now()
            };

            try {
                socketRef.current.send(JSON.stringify(messageData));
                setMessage('');
            } catch (error) {
                console.error('Error sending message:', error);
            }
        } else {
            console.error('WebSocket is not connected');
        }
    };

    // Обработка нажатия Enter
    const handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            sendMessage();
        }
    };

    return (
        <div className="chat-container">
            <h3>Community Chat</h3>

            <div className="messages">
                {messages.length > 0 ? (
                    messages.map((msg, index) => (
                        <div key={index} className="message">
                            <span className="sender">{msg.Sender}: </span>
                            <span className="content">{msg.Content}</span>
                        </div>
                    ))
                ) : (
                    <div className="no-messages">No messages yet</div>
                )}
            </div>

            <div className="message-input">
                <input
                    type="text"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    onKeyPress={handleKeyPress}
                    placeholder="Type your message..."
                    disabled={!socketRef.current || socketRef.current.readyState !== WebSocket.OPEN}
                />
                <button
                    onClick={sendMessage}
                    disabled={!message.trim() || !socketRef.current || socketRef.current.readyState !== WebSocket.OPEN}
                >
                    Send
                </button>
            </div>

            <div className="connection-status">
                Status: {socketRef.current ?
                socketRef.current.readyState === WebSocket.OPEN ? 'Connected' : 'Disconnected'
                : 'Not initialized'}
            </div>
        </div>
    );
}
