import { useState, useEffect } from 'react';

export default function Chat({ token, user }) {
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState('');
    const [socket, setSocket] = useState(null); // Сохраняем сокет в состоянии

    useEffect(() => {
        if (!token) return;

        const newSocket = new WebSocket(`ws://localhost:8081/ws?token=${token}`);
        setSocket(newSocket); // Сохраняем сокет

        newSocket.onopen = () => console.log("WebSocket connected");
        newSocket.onmessage = (e) => {
            const newMessage = JSON.parse(e.data);
            setMessages(prev => [...prev, newMessage]);
        };

        return () => newSocket.close();
    }, [token]);

    const sendMessage = () => {
        if (socket && socket.readyState === WebSocket.OPEN && message.trim()) {
            socket.send(JSON.stringify({
                Content: message,
                Type: 1,
                Sender: user.username,
                UserID: user.id
            }));
            setMessage('');
        }
    };
    // useEffect(() => {
    //     if (!token) return;
    //
    //     const socket = new WebSocket(`ws://localhost:8081/ws?token=${token}`);
    //
    //     socket.onopen = () => {
    //         console.log('WebSocket connected');
    //         setWs(socket);
    //     };
    //
    //     socket.onmessage = (e) => {
    //         const newMessage = JSON.parse(e.data);
    //         setMessages(prev => [...prev, newMessage]);
    //     };
    //
    //     return () => {
    //         socket.close();
    //     };
    // }, [token]);


    return (
        <div className="chat-container">
            <h3>Community Chat</h3>
            <div className="messages">
                {messages.map((msg, i) => (
                    <div key={i} className="message">
                        <span className="sender">{msg.Sender}:</span>
                        <span className="content">{msg.Content}</span>
                    </div>
                ))}
            </div>
            <div className="message-input">
                <input
                    type="text"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
                    placeholder="Type your message..."
                />
                <button onClick={sendMessage}>Send</button>
            </div>
        </div>
    );
}
