import { useState, useEffect, useRef } from 'react';

export default function Chat({ token, user }) {
    const [messages, setMessages] = useState([]);
    const [inputMessage, setInputMessage] = useState('');
    const wsRef = useRef(null);

    useEffect(() => {
        const socket = new WebSocket(`ws://localhost:8081/ws${token ? `?token=${token}` : ''}`);
        wsRef.current = socket;

        socket.onopen = () => {
            // Загружаем историю сообщений при подключении
            socket.send(JSON.stringify({
                type: 'get_history',
                limit: 100
            }));
        };

        socket.onmessage = (event) => {
            const data = JSON.parse(event.data);
            setMessages(prev => Array.isArray(data) ? data : [...prev, data]);
        };

        return () => socket.close();
    }, [token]);

    const sendMessage = () => {
        if (!inputMessage.trim() || !token) return;

        const message = {
            type: 1,
            content: inputMessage,
            sender: user.username,
            userId: user.id,
            timestamp: Date.now()
        };

        wsRef.current.send(JSON.stringify(message));
        setInputMessage('');
    };

    const deleteMessage = (messageId) => {
        if (!token) return;
        wsRef.current.send(JSON.stringify({
            type: 'delete_message',
            messageId,
            userId: user.id
        }));
    };

    return (
        <div className="chat-container">
            <div className="chat-header">
                <h2>Community Chat</h2>
            </div>

            <div className="messages-container">
                {messages.map((msg) => (
                    <div key={msg.id} className={`message ${msg.userId === user?.id ? 'own-message' : ''}`}>
                        <div className="message-header">
                            <span className="message-sender">{msg.sender}</span>
                            <span className="message-time">
                                {new Date(msg.timestamp).toLocaleString()}
                            </span>
                            {msg.userId === user?.id && (
                                <button
                                    className="delete-message-btn"
                                    onClick={() => deleteMessage(msg.id)}
                                >
                                    ×
                                </button>
                            )}
                        </div>
                        <div className="message-content">{msg.content}</div>
                    </div>
                ))}
            </div>

            {token ? (
                <div className="message-input-container">
                    <input
                        type="text"
                        value={inputMessage}
                        onChange={(e) => setInputMessage(e.target.value)}
                        onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
                        placeholder="Type your message..."
                    />
                    <button onClick={sendMessage}>
                        Send
                    </button>
                </div>
            ) : (
                <div className="login-notice">
                    Please login to send messages
                </div>
            )}
        </div>
    );
}
