import { useState, useEffect, useRef } from 'react';

export default function Chat({ token, user }) {
    const [messages, setMessages] = useState([]);
    const [inputMessage, setInputMessage] = useState('');
    const [status, setStatus] = useState('disconnected');
    const wsRef = useRef(null);

    useEffect(() => {
        const socket = new WebSocket(`ws://localhost:8081/ws${token ? `?token=${token}` : ''}`);
        wsRef.current = socket;

        socket.onopen = () => {
            setStatus('connected');
            if (token) {
                socket.send(JSON.stringify({ type: 'get_history', limit: 50 }));
            }
        };

        socket.onmessage = (event) => {
            const data = JSON.parse(event.data);
            setMessages(prev => Array.isArray(data) ? data : [...prev, data]);
        };

        socket.onclose = () => setStatus('disconnected');
        socket.onerror = () => setStatus('error');

        return () => socket.close();
    }, [token]);

    const sendMessage = () => {
        if (!inputMessage.trim() || !token) return;

        const message = {
            type: 1,
            content: inputMessage,
            sender: user.username,
            userId: user.id,
            timestamp: Date.now()
        };

        wsRef.current.send(JSON.stringify(message));
        setInputMessage('');
    };

    return (
        <div className="chat-container">
            <div className="chat-header">
                <h2>Community Chat</h2>
                <div className={`connection-status ${status}`}>
                    Status: {status}
                </div>
            </div>

            <div className="messages-container">
                {messages.map((msg, index) => (
                    <div key={`${msg.timestamp}-${index}`} className={`message ${msg.userId === user?.id ? 'own-message' : ''}`}>
                        <div className="message-sender">{msg.sender || 'Guest'}</div>
                        <div className="message-content">{msg.content}</div>
                        <div className="message-time">
                            {new Date(msg.timestamp).toLocaleTimeString()}
                        </div>
                    </div>
                ))}
            </div>

            {token ? (
                <div className="message-input-container">
                    <input
                        type="text"
                        value={inputMessage}
                        onChange={(e) => setInputMessage(e.target.value)}
                        onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
                        placeholder="Type your message..."
                    />
                    <button onClick={sendMessage} disabled={!inputMessage.trim()}>
                        Send
                    </button>
                </div>
            ) : (
                <div className="login-notice">
                    Please login to send messages
                </div>
            )}
        </div>
    );
}
