import { useState, useEffect, useRef } from 'react';

export default function Chat({ token, user }) {
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState('');
    const [status, setStatus] = useState('disconnected');
    const socketRef = useRef(null);
    const reconnectAttempts = useRef(0);
    const maxReconnectAttempts = 5;

    const connectWebSocket = () => {
        if (!token || reconnectAttempts.current >= maxReconnectAttempts) return;

        const socket = new WebSocket(`ws://localhost:8081/ws?token=${encodeURIComponent(token)}`);
        socketRef.current = socket;

        socket.onopen = () => {
            console.log('WebSocket connected');
            setStatus('connected');
            reconnectAttempts.current = 0;
        };

        socket.onmessage = (e) => {
            const msg = JSON.parse(e.data);
            setMessages(prev => [...prev, msg]);
        };

        socket.onerror = (e) => {
            console.error('WebSocket error:', e);
            setStatus('error');
        };

        socket.onclose = (e) => {
            console.log(`WebSocket closed: ${e.code} ${e.reason}`);
            setStatus('reconnecting');
            setTimeout(() => {
                reconnectAttempts.current += 1;
                connectWebSocket();
            }, 3000);
        };
    };

    useEffect(() => {
        connectWebSocket();

        return () => {
            if (socketRef.current?.readyState === WebSocket.OPEN) {
                socketRef.current.close(1000, 'Component unmount');
            }
        };
    }, [token]);

    const sendMessage = () => {
        if (!socketRef.current || socketRef.current.readyState !== WebSocket.OPEN) {
            console.error('Cannot send - connection not ready');
            return;
        }

        socketRef.current.send(JSON.stringify({
            type: 'message',
            content: message,
            sender: user?.username || 'Guest',
            userId: user?.id || 0
        }));
        setMessage('');
    };
    return (
        <div className="chat-container">
            <h3>Community Chat</h3>
            <div className="status">Status: {connectionStatus}</div>

            <div className="messages">
                {messages.map((msg, i) => (
                    <div key={i} className="message">
                        <span className="sender">{msg.sender}: </span>
                        <span className="content">{msg.content}</span>
                    </div>
                ))}
            </div>

            <div className="message-input">
                <input
                    type="text"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    onKeyPress={handleKeyPress}
                    placeholder="Type your message..."
                    disabled={connectionStatus !== 'connected'}
                />
                <button
                    onClick={sendMessage}
                    disabled={!message.trim() || connectionStatus !== 'connected'}
                >
                    Send
                </button>
            </div>
        </div>
    );
}
